package Variables;

public class IntegerType {
	public static void primitives() {
		//literals demo
		int inthex = 0x0031;
		System.out.println("Print inthex: " + inthex);
		int intBinary = 0b00010001;
		System.out.println("Print intBinary: "+ intBinary);
		int intUnderscores = 1_2_3_456;
		System.out.println("Print intUnderscores: " + intUnderscores);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		IntegerType.primitives();
	}

}
